import React, { useState } from "react";
import API from "../api/api";

export default function ProfitCalculator() {
  const [input, setInput] = useState({
    crop: "",
    area: "",
    cost: ""
  });
  const [result, setResult] = useState(null);

  const calc = async () => {
    const res = await API.post("/profit/", input);
    setResult(res.data);
  };

  return (
    <div className="card">
      <h2>ðŸ’¸ Profit Calculator</h2>

      <input placeholder="Crop" onChange={e => setInput({...input, crop:e.target.value})}/>
      <input placeholder="Area (acres)" onChange={e => setInput({...input, area:e.target.value})}/>
      <input placeholder="Total Cost" onChange={e => setInput({...input, cost:e.target.value})}/>

      <button onClick={calc}>Calculate</button>

      {result && (
        <>
          <p>Revenue: â‚¹{result.expected_revenue}</p>
          <p>Net Profit: â‚¹{result.net_profit}</p>
          <strong>{result.verdict}</strong>
        </>
      )}
    </div>
  );
}
