import React, { useState } from "react";
import API from "../api/api";

export default function PriceAnalyzer() {
  const [crop, setCrop] = useState("");
  const [data, setData] = useState(null);

  const analyze = async () => {
    const res = await API.get(`/price-analysis/?crop=${crop}`);
    setData(res.data);
  };

  return (
    <div className="card">
      <h2>ðŸ“ˆ Market Price Analyzer</h2>

      <input
        placeholder="Crop name"
        value={crop}
        onChange={e => setCrop(e.target.value)}
      />
      <button onClick={analyze}>Analyze</button>

      {data && (
        <>
          <p>Avg Price: â‚¹{data.avg_price}/quintal</p>
          <p>Trend: {data.trend}</p>
          <p>Best Month: {data.best_month}</p>
        </>
      )}
    </div>
  );
}
