import React, { useState } from "react";
import API from "../api/api";

export default function SubsidySuggestions() {
  const [crop, setCrop] = useState("");
  const [subs, setSubs] = useState([]);

  const fetchSubs = async () => {
    const res = await API.get(`/subsidy/?crop=${crop}`);
    setSubs(res.data);
  };

  return (
    <div className="card">
      <h2>üèõÔ∏è Govt Subsidies</h2>

      <input
        placeholder="Crop"
        value={crop}
        onChange={e => setCrop(e.target.value)}
      />
      <button onClick={fetchSubs}>Check</button>

      <ul>
        {subs.map((s, i) => (
          <li key={i}>{s}</li>
        ))}
      </ul>
    </div>
  );
}
